mapper.select:
select num,board_userid,subject,content,filename fileurl,regdate,board_count,board_type from totalboard
 
 where board_type=#{board_type} 
 
 
 
order by regdate desc limit #{startrow},#{limit}

select num,board_userid,subject,content,filename fileurl,regdate,board_count,board_type from totalboard
 
 
 where board_type=${board_type} 
 

 
 
 and ${searchType} like '%${searchContent}%'
 
 
 
 
order by regdate desc limit #{startrow},#{limit}




  order by bestcnt desc
